import React from "react";

const Product = ({ product, onAddToCart }) => {
  return (
    <div style={{ border: "1px solid #ccc", padding: "10px", width: "150px" }}>
      <h3>{product.name}</h3>
      <p>${product.price}</p>
      <button onClick={() => onAddToCart(product)}>Add to Cart</button>
    </div>
  );
};

export default Product;
